styles:
.title.active {
  color: red
}

.async-component-box {
  position: relative;
}

.clear-input-value {
  position: absolute;
  right: 8px;
  top: 0;
  cursor: pointer;
  width: 12px;
  height: 30px;
  background: url(data:image/png;base64,) no-repeat center center transparent;
}

.clear-input-value:hover {
  background: url(data:image/png;base64,) no-repeat center center transparent;
}

input[type=text] {
  border: solid 1px #ccc;
  height: 30px;
  line-height: 30px;
  padding: 0 30px 0 10px;
  border-radius: 4px;
  transition: none;
  background: none;
  outline: none;
}

input[type=text].dc-valid {
  border-color: #3FB992;
}

input[type=text].dc-invalid {
  border-color: #FF3B3B;
}

input[type=text]:focus {
  border-color: #2BB1FF;
}

.title {
  cursor: pointer;
}

.complete-container {
  position: absolute;
  top: 28px;
  border: solid 1px #ccc;
  background-color: #ffffff;
  box-shadow: 0px 0px 5px 2px rgba(0, 0, 0, .2);
  z-index: 1100;
  max-height: 200px;
  overflow: auto;
}

.source-title {
  max-width: 100%;
  height: 30px;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  cursor: pointer;
  padding: 0 8px;
}

.source-title:hover {
  background-color: #edf0f5;
}

.source-title.selected {
  background-color: #0081cc;
  color: #ffffff;
}

.source-title.source-title.active {
  color: rebeccapurple;
}
