styles:
.dc-checkbox {
  position: relative;
  display: inline-block;
  min-width: 20px;
}

.dc-checkbox-inner {
  display: flex;
  align-items: center;
  height: 25px;
  line-height: 25px;
}

.dc-checkbox-input {
  visibility: hidden;
  position: absolute;
  width: 1px !important;
  height: 1px !important;
  overflow: hidden;
}

.dc-checkbox-label {
  position: relative;
  cursor: pointer;
  width: 16px;
  height: 16px;
  background: #fff;
  border: 1px solid #c8c8c8;
  border-radius: 3px; /* W3C syntax */
  margin: 0;
  flex: 0 0 auto;
}

.dc-checkbox-label:not(disabled):hover {
  background: #0081CC;
}

.dc-checkbox-input:disabled:hover + label:hover {
  background: transparent;
}

.dc-checkbox-label:after {
  opacity: 0;
  content: '';
  position: absolute;
  top: 3px;
  left: 3px;
  width: 9px;
  height: 5px;
  background: transparent;
  border: 2px solid #fff;
  border-top: none;
  border-right: none;
  -webkit-transform: rotate(-45deg);
  -moz-transform: rotate(-45deg);
  -o-transform: rotate(-45deg);
  -ms-transform: rotate(-45deg);
  transform: rotate(-45deg);
}

.dc-checkbox-label:hover:after {
  opacity: 0.6;
}

.dc-checkbox-input:disabled,
.dc-checkbox-input:disabled + label {
  cursor: not-allowed;
  background-color: #f5f5f5;
  opacity: 0.4;
}

.dc-checkbox-input:disabled {
  background: #ffffff;
}

.dc-checkbox-input:checked + label {
  background: #0081CC;
}

.dc-checkbox-input:checked + label:after {
  opacity: 1.0;
}

.dc-checkbox-text {
  cursor: pointer;
  padding: 0 5px;
  margin: 0 0 0 5px;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  flex: 1 1 auto;
}

.dc-checkbox-text:hover {
  background-color: #edf0f5;
  /*color: #fff;*/
}
