styles:
.date-picker-box {
  position: relative;
  display: inline-block;
  width: 150px;
}

.date-picker-outer {
  display: flex;
  align-items: center;
  flex-direction: row;
  height: 32px;
  border: 1px solid #ccc;
  border-radius: 2px;
}

.date-picker-outer .date-picker-input {
  flex: 1;
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: nowrap;
  height: 32px;
  cursor: pointer;
}

.date-picker-outer .show {
  border: solid 4px transparent;
  border-top-width: 6px;
  border-top-color: #333;
  width: 0;
  height: 0;
  flex: none;
  cursor: pointer;
  margin-right: 8px;
  margin-top: 4px;
}

.date-picker-outer .show.active {
  border: solid 4px transparent;
  border-bottom-width: 6px;
  border-bottom-color: blue;
  border-top-width: 0;
  width: 0;
  height: 0;
  flex: none;
  margin-top: 0;
}

.date-picker-outer .clear {
  flex: none;
  cursor: pointer;
  width: 20px;
  height: 30px;
  margin-right: 4px;
  background: url(data:image/png;base64,) no-repeat center center transparent;
}

.date-picker-outer .clear:hover {
  background: url(data:image/png;base64,) no-repeat center center transparent;
}

.date-picker-drop {
  position: absolute;
  width: 280px;
  min-height: 254px;
  background-color: white;
  box-shadow: 0 0 5px gray;
  z-index: 1000;
  right: 0;
  user-select: none;
}

/*.date-picker-drop.showToday{

}*/

.date-picker-drop.offset-left {
  right: auto;
  left: 0;
}

.control-bar {
  display: flex;
  padding: 0 6px;
  height: 40px;
  align-items: center;
  justify-content: center;
  background-color: #fff;
  border-bottom: 1px solid #eee;
}

.control-title {
  display: flex;
  flex: 1;
  align-items: center;
  justify-content: center;
}

.title-icon {
  cursor: pointer;
  height: 40px;
  line-height: 40px;
  margin-right: 8px;
  font-weight: 500;
}

.title-icon.has-icon {
  background: url('data:image/png;base64,') no-repeat right center transparent;
  padding-right: 11px;
}

.title-icon.has-icon.active {
  background-image: url("data:image/png;base64,");
}

.material-icon {
  cursor: pointer;
  width: 20px;
  height: 40px;
  opacity: 0.8;
}

.material-icon:hover {
  opacity: 1;
}

.material-icon.de-year {
  background: url("data:image/png;base64,") no-repeat center transparent;
}

.material-icon.in-year {
  background: url("data:image/png;base64,") no-repeat center transparent;
}

