.date-drop-content {
  align-items: center;
  justify-content: center;
  display: none;
  margin-top: 13px;
}

.date-drop-content.active {
  display: flex;
}

.date-drop-content table {
  border-spacing: 0;
  border: none;
}

.table-day {
}

.table-day th, td {
  text-align: center;
  position: relative;
  font-weight: normal;
}

.date {
  display: block;
  width: 24px;
  height: 24px;
  text-align: center;
  line-height: 24px;
  margin: 2px 6px;
  cursor: pointer;
  border-radius: 2px;
  background-color: #fff;
  opacity: 0.3;
}

.month {
  display: block;
  width: 45px;
  height: 24px;
  line-height: 24px;
  margin: 11px 18px;
  text-align: center;
  cursor: pointer;
  border-radius: 2px;
}

.year {
  display: block;
  width: 60px;
  height: 24px;
  line-height: 24px;
  margin: 11px 10px;
  text-align: center;
  cursor: pointer;
  border-radius: 2px;
}

.date.inMonth {
  opacity: 1;
}

.date:hover,
.month:hover,
.year:hover {
  background-color: #eeeeee;
}

.date.active,
.month.active,
.year.active {
  background-color: #1890ff;
  color: #fff;
}

.date.disabled {
  cursor: not-allowed;
  text-decoration: line-through;
}

.month.disabled {
  cursor: not-allowed;
  text-decoration: line-through;
}

.year.disabled {
  cursor: not-allowed;
  text-decoration: line-through;
}
