styles:
ul, li {
  list-style: none;
  margin: 0;
  padding: 0;
}

.date-more {
  display: inline-block;
  width: 8px;
  height: 10px;
  border: solid 4px transparent;
  border-top-width: 6px;
  border-top-color: #666;
  position: relative;
  top: 3px;
}
.showYearMonthMore .date-more{
  top: 0;
  transform:rotate(180deg);
  -ms-transform:rotate(180deg); /* Internet Explorer */
  -moz-transform:rotate(180deg); /* Firefox */
  -webkit-transform:rotate(180deg); /* Safari 和 Chrome */
  -o-transform:rotate(180deg); /* Opera */
}

.form-control {
  display: block;
  width: 100%;
  height: 34px;
  padding: 0 0 0 5px;
  font-size: inherit;
  line-height: 1.42857143;
  color: #555;
  background-color: #fff;
  background-image: none;
  border: 1px solid #ccc;
  border-radius: 4px;
  -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075);
  box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075);
  -webkit-transition: border-color ease-in-out .15s, -webkit-box-shadow ease-in-out .15s;
  -o-transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;
  transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;
}

input.form-control {
  border: 1px solid #ccc;
}

input::placeholder {
  color: #bbb
}

div.date-picker-box {
  position: relative;
  display: inline-block;
  width: 150px;
}

div.date-picker-input {
  position: relative;
  display: inline-block;
  width: 100%;
  background-color: white;
  color: #333;
  font-size: 12px;
}

div.date-picker-input input {
  height: 30px;
  border-radius: 3px;
}

div.date-picker-input i.show {
  position: absolute;
  right: 2px;
  top: 0;
  font-size: inherit;
  cursor: pointer;
  width: 20px;
  height: 30px;
  background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA4AAAAOCAYAAAAfSC3RAAAAYklEQVQokdWQUQrAIAxDU9m/O6XHn97A/awQ2w4s+1qgIA3h1UhrDaQB4ESsxSsA5jOsSTNoX3VfTOCN5rzDUCLVyLPEbSlRkjn3Ry6DS3MFfj5VJRvvkPijU1PEDuBK5voNJIwdJa4Jkp0AAAAASUVORK5CYII=) no-repeat center center transparent;
}

div.date-picker-input i.clear {
  position: absolute;
  right: 22px;
  top: 0;
  font-size: inherit;
  cursor: pointer;
  width: 20px;
  height: 30px;
  background: url(data:image/png;base64,) no-repeat center center transparent;
}
div.date-picker-input i.clear:hover{
  background: url(data:image/png;base64,) no-repeat center center transparent;
}

div.date-picker {
  position: absolute;
  width: auto;
  min-height: 220px;
  background-color: white;
  box-shadow: 0px 0px 5px gray;
  z-index: 1000;
  right: 0;
}

div.date-picker.offset-left {
  right: auto;
  left: 0;
}

.years-month-ul {
  position: absolute;
  width: 150px;
  top: 30px;
  right: 0;
  margin-right: 40px;
  background-color: white;
  box-shadow: 0 0 5px gray;
  z-index: 1001;
}

.years-month-ul li {
  height: 22px;
  display: flex;
}

.years-month-ul li span {
  width: 50%;
  align-items: center;
  justify-content: center;
  text-align: center;
  cursor: pointer;
  font-size: 12px;
  border-radius: 3px;
}

.years-month-ul li span:hover {
  background-color: #eeeeee;
}

.years-month-ul li span.selected {
  background-color: #0081cc;
  color: #fff;
}

.nav-bar ul {
  list-style: none;
  padding-top: 10px;
  padding-left: 10px;
}

.nav-bar ul li {
  font-size: 13px;
  background: #f5f5f5;
  border: 1px solid #f5f5f5;
  border-radius: 4px;
  color: #1165E5;
  padding: 3px 12px;
  margin-bottom: 8px;
}

.nav-bar ul li.active, .nav-bar ul li:hover {
  background: #0081cc;
  border: 1px solid #0081cc;
  color: #ffffff;
}

div.calendar .control-bar {
  width: 100%;
  display: flex;
  height: 35px;
  align-items: center;
  justify-content: center;
  background-color: #eeeeee;
}

div.calendar .control-bar .material-icons {
  cursor: pointer;
  font-size: 21px;
  margin: 2px;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 30px;
  height: 30px;
  font-weight: bold;
  opacity: 0.7;
}
div.calendar .control-bar .material-icons:hover{
  opacity: 1;
}

div.calendar .control-bar .material-icons.de-year {
  background: url(data:image/png;base64,) no-repeat center center transparent;
}

div.calendar .control-bar .material-icons.de-month {
  background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAYAAAAKCAYAAACXDi8zAAAAgUlEQVQImU3OIQ9BcRiF8d/9M8FmJmiiTyIg2yiyq0qq7BNIkmTIguCDqDdKos2uct/tPunsnJ3tyfI8V6ONC3bNWtnCDVP0UlU2cMIEBRYJGQ6Y440RioQ9VvhgjBfEIygjJGxxRBcPDGMoscYVfTwxCN0fluhUZufQhS9muGPzB2YAFnw+Bp0mAAAAAElFTkSuQmCC) no-repeat center center transparent;
}

div.calendar .control-bar .material-icons.in-month {
  background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAYAAAAKCAYAAACXDi8zAAAAhElEQVQImU3OoQ5BYQDF8d/9mCppokfwBIKZaENRfZuo6B5BEkTFZjoTeA/1NkUSbHaV+839p7P9d3ZOFmPsYo0x3koCNhjihEZVTJFjgD1qSeTo4YkJtsjqZfOBPu6Y4xX8KSo5S6KDK5rYYRXQxg0tHLFAEXAo5QUzfNOrJc4Y4ZNGfuOgGIESFz17AAAAAElFTkSuQmCC) no-repeat center center transparent;
}

div.calendar .control-bar .material-icons.in-year {
  background: url(data:image/png;base64,) no-repeat center center transparent;
}

div.calendar .control-bar .monthTitle {
  font-size: inherit;
  font-weight: normal;
  width: 160px;
  color: #333;
  text-align: center;
  cursor: pointer;
  height: 30px;
  line-height: 30px;
}

div.calendar .control-bar .monthTitle:hover {
  color: #0081cc;
}

div.calendar .control-bar .monthTitle:hover .date-more {
  color: #0081cc;
  border-top-color: #0081cc;
}

div.calendar .days {
  width: 100%;
  align-items: center;
  justify-content: center;
  display: flex;
  font-size: inherit;
}

table {
  border-spacing: 0;
}

th, td {
  text-align: center;
  position: relative;
  font-weight: normal;
}

th {
  color: #333;
}

tr > td.disabled {
  color: #999999;
  text-decoration: line-through;
  cursor: not-allowed;
}

tr > td.range {
  background-color: #ebf4f8;
  border-color: transparent;
}

tr > td.active, tr > td.active:hover {
  border-radius: 4px;
  background-color: #0081cc !important;
  color: white;
}

tr > td:hover, .control-bar .material-icons:hover {
  background-color: #eeeeee !important;
  border-radius: 4px;
}

.date {
  width: 30px;
  height: 30px;
  text-align: center;
  line-height: 30px;
}

.cancelArea {
  position: fixed;
  width: 100vw;
  height: 100vh;
  top: 0;
  left: 0;
  z-index: 9;
}

.pointer {
  cursor: pointer;
}

.pull-left {
  float: left !important;
}
