styles:
.draw-back {
  position: fixed;
  width: 100%;
  height: 100%;
  opacity: 0.2;
  background: #000;
  z-index: 1002;
}

.draw-container {
  opacity: 1;
  width: 66%;
  position: fixed;
  height: 100vh;
  right: 0;
  max-width: 0;
  transition: max-width 0.5s ease-in, color 0.3s ease-out;
  box-shadow: -2px 0px 15px 0px #888888;
  background: #fcfcfc;
  overflow-x: hidden;
  overflow-y: auto;
  z-index: 1003;
  display: flex;
  flex-direction: column;
}

.dc-draw-header {
  /*box-shadow: 0px 0px 40px 0px rgba(0, 0, 0, .1);*/
  padding: 0 20px;
  font-size: 18px;
  font-weight: bold;
  height: 50px;
  line-height: 50px;
  flex-grow: 0;
  flex-shrink: 0;
  position: relative;
  border-bottom: solid 1px #ccc;
}

.draw-img {
  margin: 0 5px 4px 0;
}

.dc-draw-title {
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: nowrap;
  width: calc(100% - 46px);
}

.dc-draw-close {
  background: url(data:image/png;base64,) no-repeat center;
  width: 31px;
  height: 31px;
  position: absolute;
  right: 15px;
  top: 10px;
  cursor: pointer;
}

.draw-container.show {
  max-width: 100%;
}

.dc-draw-content {
  flex: 1;
  height: 100px;
}

::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track,
::-webkit-scrollbar-thumb {
  border-radius: 999px;
  border: 0 solid transparent;
}

::-webkit-scrollbar-thumb {
  min-height: 20px;
  max-height: 100px;
  box-shadow: 0 0 0 5px rgba(201, 201, 201, 1) inset;
}

::-webkit-scrollbar-corner {
  background: transparent;
}
