styles:
.dc-header-container {
      height: 60px;
    }

    .dc-header {
      position: fixed;
      top: 0;
      right: 0;
      width: calc(100% - 249px);
      height: 60px;
      background: #1d2b40;
      z-index: 1000;
    }

    .dc-header.zoomout {
      width: calc(100% - 40px);
    }

    .toggle-left-menu {
      width: 38px;
      height: 18px;
      cursor: pointer;
    }

    .toggle-arrow {
      float: left;
      border: solid 4px transparent;
      border-right-width: 6px;
      border-right-color: #fff;
      width: 0;
      height: 0;
      margin: 5px 0 0 2px;
    }

    .toggle-arrow2 {
      float: right;
      border: solid 4px transparent;
      border-left-width: 6px;
      border-left-color: #fff;
      width: 0;
      height: 0;
      margin: 5px 0 0 5px;
    }

    .toggle-square {
      float: right;
      width: 20px;
      height: 18px;
      border: solid 2px #fff;
      border-left: none;
      border-right: none;
    }

    .toggle-square > div {
      width: 100%;
      height: 8px;
      border-bottom: solid 2px #fff
    }

    .search {
      width: 310px;
      height: 30px;
      padding: 0 0 0 10px;
      background: url(data:image/png;base64,) no-repeat 280px center #566070;
      border-radius: 2px;
      margin-left: 23px;
      position: relative;
    }

    .search:hover {
      background: url(data:image/png;base64,) no-repeat 280px center #566070;
    }

    .search > input {
      width: 260px;
      height: 27px;
      border: none;
      transition: none;
      background: none;
      color: #fff;
      opacity: 0.7;
    }

    .search > input:focus {
      border: none;
      transition: none;
      background: none;
    }

    .search > span {
      cursor: pointer;
      display: block;
      width: 30px;
      height: 30px;
      float: right;
    }

    li {
      list-style: none;
      margin: 0;
      padding: 0;
    }

    .search-result {
      position: absolute;
      left: 0;
      top: 29px;
      width: 310px;
      height: auto;
      max-height: 200px;
      overflow: auto;
      border: 1px solid #ccc;
      background: #fff;
    }

    .search-result > li {
      cursor: pointer;
      width: 100%;
      height: 30px;
      overflow: hidden;
      text-overflow: ellipsis;
      white-space: nowrap;
      line-height: 30px;
      text-indent: 10px;
    }

    .search-result > li:hover {
      color: #fff;
      background-color: #0081cc;
    }

    .user {
      display: table;
      color: #fff;
      cursor: pointer;
    }

    .user > div {
      display: table-cell;
      vertical-align: middle;
    }

    .avatar {
      width: 16px;
      height: 16px;
      background: url(data:image/png;base64,) no-repeat left 2px;
    }

    .user-menu {
      padding: 0 7px 3px 7px;
    }

    .user-menu-name {
      max-width: 150px;
      overflow: hidden;
      text-overflow: ellipsis;
      white-space: nowrap;
    }

    .user-menu > ul {
      position: absolute;
      right: 0;
      top: 25px;
      box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 0.2);
    }

    .user-menu > ul > li {
      min-width: 82px;
      height: 30px;
      padding: 8px 10px 0 10px;
      color: #333;
      font-size: 12px;
      background: #fff;
      list-style: none;
      text-align: left;
      line-height: 12px;
      border-radius: 3px 3px 0 0
    }

    /*.user-menu > ul > li:nth-child(even) {*/
    /*background: #edf0f5;*/
    /*}*/

    .user-menu > ul > li:hover {
      background: #0081cc;
      color: #fff;
    }

    .user-menu > ul > li:last-child {
      border-radius: 3px
    }

    .double-arrow {
      position: absolute;
      right: -12px;
      width: 10px;
      height: 100%;
      background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAHCAYAAAAxrNxjAAAAYElEQVQYlXXPUQ2AMAyE4UpAAhKQgAQkTAIScIAEpCABSR8vHVkW9tCkvV7+XgM7ZsSgNiyBFffA9O2qcCS5Nc14MLXGSHHJfkpSnaMn1AgXSnuhz1SSfPZ5/x7oswbiBUJg3DZWt/ucAAAAAElFTkSuQmCC) no-repeat center transparent;
      z-index: 10;
    }
