styles:
.dc-input-default {
  height: 32px;
  line-height: 30px;
  padding: 0 30px 0 10px;
  border-radius: 4px;
  border: 1px solid #ccc;
  font-size: 14px;
  color: #333;
  width: 100%;
  box-shadow: none;
}

textarea.dc-input-default {
  height: 50px;
}

.clear-input-value {
  position: absolute;
  right: 8px;
  top: 0;
  cursor: pointer;
  width: 12px;
  height: 32px;
  background: url(data:image/png;base64,) no-repeat center center transparent;
}

.clear-input-value:hover {
  background: url(data:image/png;base64,) no-repeat center center transparent;
}

input.dc-input-default::placeholder,
textarea.dc-input-default::placeholder {
  color: #bbb;
  font-size: 12px;
}

.dc-input-default.dc-valid {
  border-color: #3FB992;
}

.dc-input-default.dc-invalid {
  border-color: #FF3B3B;
}

.dc-input-default:focus {
  border-radius: 4px;
  border-color: #2BB1FF;
  transition: none;
  background: none;
  outline: none;
}

.dc-input-default[readonly],
.dc-input-default[disabled] {
  background-color: #F0F0F0;
}

.numtip {
  position: absolute;
  right: 8px;
  bottom: 8px;
  color: #666;
}

.numtip strong {
  color: #2bb1ff;
  font-weight: normal;
  margin-right: 2px;
}

.numtip em {
  font-style: normal;
  margin-left: 2px;
}

.numtip strong.tip {
  color: #f95f5b;
}
