styles:
.dc-left-menu-container {
  width: 250px;
  height: 100vh;
  position: fixed;
  left: 0;
  top: 0;
  z-index: 1001;
  color: #fff
}

.scroll {
  height: calc(100vh - 60px);
  overflow-y: auto;
}

.menuClose .scroll {
  overflow: hidden;
}

::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track,
::-webkit-scrollbar-thumb {
  border-radius: 999px;
  border: 0 solid transparent;
}

::-webkit-scrollbar-thumb {
  min-height: 20px;
  box-shadow: 0 0 0 5px rgba(255, 255, 255, 0.2) inset;
  opacity: 0.2;
}

::-webkit-scrollbar-corner {
  background: transparent;
}

.dc-left-menu-container.menuClose {
  width: 45px;
}

.dc-left-menu {
  width: 100%;
  height: 100vh;
  background: #294265;
  padding-bottom: 0;
  margin-bottom: 0;
}

.logo {
  width: 100%;
  height: 60px;
  background: url(data:image/png;base64,) no-repeat center 20px #1d2b40;
  background-position: 40px 15px;
  background-size:30px 27px;
  font-size: 18px;
  font-weight: bolder;
  text-align: center;
  padding: 15px 0 0 40px;
  /*background-size: 40px 30px;*/
}

.small-logo {
  width: 100%;
  height: 60px;
  background: url(data:image/png;base64,) no-repeat center #1d2b40;
  background-size: 70%
}

.sub-menu {
  background-color: #172b47;
}

.level {
  position: relative;
  height: 45px;
  cursor: pointer;
}

.level > span {
  display: block;
  padding-left: 30px;
  margin-left: 30px;
  width: 175px;
  line-height: 45px;
  background-position: left center;
  background-repeat: no-repeat;
  cursor: pointer;
  outline: none;
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
}

.level-3 > span {
  width: auto;
  padding-left: 0;
  margin-right: 30px;
}

.left-arrow {
  background: url("data:image/png;base64,") no-repeat 210px center;
}

.bottom-arrow {
  background: url("data:image/png;base64,") no-repeat 210px center;
}

.dc-left-menu-container.menuClose .level > span {
  margin-left: 15px;
}

.menuClose .level {
  overflow: hidden;
}

.level:hover,
.level.menuActive {
  background-color: #0081cc;
  opacity: 1;
}

.level-1 {
  font-weight: bold;
  font-size: 15px;
}

.level-2,
.level-3 {
  opacity: 0.7;
  font-size: 14px;
  font-weight: normal;
}

.third-menu {
  position: absolute;
  min-width: 250px;
  max-width: 600px;
  height: 100%;
  left: 250px;
  top: 0;
  background: #172b47;
  z-index: 30000;
  padding: 26px 0;
  overflow: auto;
}

.dc-left-menu-container.menuClose .third-menu {
  left: 40px;
}
