styles:
.show-box {
  display: flex;
  flex-direction: row;
  height: 25px;
}

.show-action-left,
.show-action-right {
  flex-shrink: 0;
  flex-grow: 0;
  width: 30px;
  height: 25px;
  cursor: pointer;
}

.show-action-left {
  margin-right: 10px;
  background: url(data:image/png;base64,) no-repeat center center transparent;
}

.show-action-left:hover {
  background: url(data:image/png;base64,) no-repeat center center transparent;
}

.show-action-right {
  margin-left: 10px;
  background: url(data:image/png;base64,) no-repeat center center transparent;
}

.show-action-right:hover {
  background: url(data:image/png;base64,) no-repeat center center transparent;
}

.show-ul-outer {
  flex: 1;
  position: relative;
  overflow: hidden;
}

.show-ul {
  display: flex;
  flex-direction: row;
  align-items: center;
  font-size: 14px;
  position: absolute;
}

.show-item {
  position: relative;
  height: 25px;
  line-height: 22px;
  color: #333;
  flex-shrink: 0;
  flex-grow: 0;
  background: #fff;
  text-align: left;
  padding: 0 20px 0 6px;
  border: 1px solid #0081cc;
  margin-right: 20px;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
  border-radius: 3px;
}

.show-item.no-del {
  padding: 0 6px;
}

.show-item-del {
  position: absolute;
  right: 6px;
  width: 10px;
  top: 0;
  height: 25px;
  cursor: pointer;
  background: url(data:image/png;base64,) no-repeat center transparent;
}
