styles:
.title {
  padding: 30px 50px 30px;
  min-height: 125px;
  text-align: center;
  display: block;
  overflow: hidden;
  font-size: 14px;
  white-space: nowrap;
  text-overflow: ellipsis;
}

img {
  margin-right: 15px;
  width: 30px;
  height: 30px;
}

.tip-type {
  text-align: left;
  display: flex;
  align-items: center;
  justify-content: left;
}

.tip-type b {
  font-size: 18px;
  color: #333;
}

.tip-msg {
  white-space: normal;
  word-break: break-all;
  font-size: 14px;
  color: #333;
  line-height: 30px;
  padding-left: 45px;
  margin-top: 5px;
  text-align: left;
  max-height: 200px;
  overflow: auto;
}
.tip-msg-list{
  white-space: normal;
  text-indent: 0;
}

.tipBtn {
  height: 70px;
  display: flex;
  justify-content: center;
  align-items: center;
  box-shadow: rgba(0, 0, 0, 0.1) 0px 0px 10px 0px;
}

dc-button:nth-child(2) {
  margin-left: 15px;
}
