styles:
.dc-modal-backdrop,
    .dc-modal-click {
      position: fixed;
      width: 100%;
      height: 100%;
      opacity: 0.2;
      background: #000;
      left: 0;
      top: 0;
      right: 0;
      bottom: 0;
      z-index: 1004;
    }

    .dc-modal-click {
      opacity: 1;
      background: transparent;
      z-index: 1010;
      overflow-y: auto;
      display: block;
      display: -ms-flexbox;
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: center
    }

    .dc-modal-box {
      width: 60%;
      box-shadow: 5px 5px 20px 0 rgba(0, 0, 0, .2);
      margin: 0 auto;
      background: #fff;
    }

    .dc-modal-header {
      /*box-shadow: 0px 0px 40px 0px rgba(0, 0, 0, .1);*/
      padding: 0 20px;
      font-size: 18px;
      font-weight: bold;
      height: 50px;
      line-height: 50px;
      position: relative;
    }

    .dc-modal-title {
      text-overflow: ellipsis;
      overflow: hidden;
      white-space: nowrap;
      width: calc(100% - 46px);
      display: flex;
      align-items: center;
    }

    .dc-modal-title img {
      margin-right: 5px;
    }

    .dc-modal-close {
      background: url(data:image/png;base64,) no-repeat center;
      width: 31px;
      height: 31px;
      position: absolute;
      right: 15px;
      top: 10px;
      cursor: pointer;
    }

    .dc-modal-content {
      min-height: 100px;
    }
