styles:
.radio-box {
  display: flex;
  justify-content: flex-start;
}

.form-radio {
  margin: 0 20px 0 0;
  font-size: 12px;
  cursor: pointer;
  display: flex;
  align-items: center;
}

.form-radio.radio-disabled {
  cursor: not-allowed;
}

.form-radio-text {
  margin-left: 5px;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  padding: 0 5px;
  flex: 1;
}

.form-radio:hover .form-radio-text {
  background: #edf0f5;
  /*color: #fff;*/
}

.simulation {
  width: 13px;
  height: 13px;
  border: 1px solid #cdcdcd;
  border-radius: 50%;
  vertical-align: middle;
  position: relative;
  flex-grow: 0;
  flex-shrink: 0;
}

.simulation:after {
  content: '';
  width: 6px;
  height: 6px;
  background: #0081cc;
  border-radius: 50%;
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  margin: auto;
  opacity: 0;
}

input[type='radio'] {
  vertical-align: middle;
  display: none;
}

input[type='radio']:checked + .simulation {
  border-color: #0081cc;
}

input[type='radio']:checked + .simulation:after {
  opacity: 1;
}

input[type='radio']:disabled + .simulation,
input[type='radio']:disabled + .simulation:after {
  opacity: 0.4;
}

.form-radio input[type='radio']:disabled + .simulation {
  border-color: #cdcdcd;
}

.form-radio input[type='radio']:disabled + .simulation:after {
  display: none;
}
