styles:
.dc-select-container {
      width: 160px;
      height: 30px;
      font-size: 14px;
      color: #333;
      background: #fff;
      /*margin: 0 10px;*/
      padding: 0;
      display: inline-block;
      position: relative
    }

    .dc-select-container:hover {
      /*border-color: #3d70b2*/
    }

    .dc-select-container:active {
      /*border-color: #0081cc*/
    }

    .dc-select-container.disable {
      background: #f5f5f5;
      border-color: #ccc;
      color: #999
    }

    .dc-select-input {
      /*padding: 5px 10px;*/
      height: 30px;
      position: relative;
      /*display: flex;*/
      /*flex-direction: row;*/
    }

    .dc-select-input > input {
      outline: none;
      width: 100%;
      padding: 0 30px 0 10px;
      height: 30px;
      line-height: 30px;
      background: none;
      border: 1px solid #ccc;
      border-radius: 4px;
      position: relative;
      z-index: 1;
    }

    .dc-select-input > input.dc-valid {
      border-color: #3FB992;
    }

    .dc-select-input > input.dc-invalid {
      border-color: #FF3B3B;
    }

    .dc-select-input > input.focus,
    .dc-select-input > input:focus {
      border-color: #2BB1FF;
    }

    .dc-select-input > input:read-only {
      cursor: default
    }

    .dc-select-input > span {
      display: block;
      position: absolute;
      width: calc(100% - 40px);
      height: 18px;
      left: 10px;
      top: 5px;
      line-height: 18px;
      overflow: hidden;
      text-overflow: ellipsis;
      white-space: nowrap;
      z-index: 0;
    }

    .clear-input-value {
      position: absolute;
      right: 20px;
      top: 0;
      cursor: pointer;
      width: 12px;
      height: 30px;
      z-index: 2;
      background: url(data:image/png;base64,) no-repeat center center transparent;
    }

    .clear-input-value:hover {
      background: url(data:image/png;base64,) no-repeat center center transparent;
    }

    .dc-select-btn {
      width: 0;
      height: 30px;
      position: absolute;
      right: 0;
      top: 0;
      padding: 12px 15px 0 5px;
      cursor: pointer;
      z-index: 3;
    }

    .dc-select-container.disable .dc-select-btn {
      cursor: default;
    }

    .dc-select-arrow {
      border: solid 4px transparent;
      border-top-width: 6px;
      border-top-color: #333;
      width: 0;
      height: 0;
    }

    .showDropFlagBtn .dc-select-arrow {
      top: 0;
      transform: rotate(180deg);
      -ms-transform: rotate(180deg); /* Internet Explorer */
      -moz-transform: rotate(180deg); /* Firefox */
      -webkit-transform: rotate(180deg); /* Safari å’Œ Chrome */
      -o-transform: rotate(180deg); /* Opera */
    }

    .dc-select-container.disable .dc-select-arrow {
      border-top-color: #ccc;
    }

    .dc-select-drop {
      position: absolute;
      top: 30px;
      left: -1px;
      width: calc(100% + 2px);
      box-shadow: 0px 0px 10px 2px rgba(0, 0, 0, .2);
      background: #fff;
      list-style: none;
      border-radius: 3px;
      overflow-y: auto;
      overflow-x: hidden;
      z-index: 1000;
    }

    .dc-select-drop.drop-up {
      top: auto;
      bottom: 25px;
    }

    .selected {
      background-color: #0081cc;
      color: #ffffff;
    }

    .dc-select-drop > li {
      width: 100%;
      height: 30px;
      line-height: 26px;
      cursor: pointer;
      overflow: hidden;
      text-overflow: ellipsis;
      white-space: nowrap;
      padding: 0 11px
    }

    .dc-select-drop > li:hover {
      color: #333333;
      background: #edf0f5;
    }

    .dc-select-drop > li.selected {
      background-color: #0081cc;
      color: #ffffff;
    }
