styles:
ul, li {
  list-style: none;
  margin: 0;
  padding: 0;
}

.step-box {
  display: flex;
  flex-direction: row;
  height: 55px;
}

.step-action-left,
.step-action-right {
  flex-shrink: 0;
  flex-grow: 0;
  width: 30px;
  height: 30px;
  cursor: pointer;
}

.step-action-left {
  margin-right: 10px;
  background: url(data:image/png;base64,) no-repeat center center transparent;
}

.step-action-right {
  margin-left: 10px;
  background: url(data:image/png;base64,) no-repeat center center transparent;
}

.step-ul-outer {
  flex: 1;
  position: relative;
  overflow: hidden;
}

.step-ul {
  display: flex;
  flex-direction: row;
  align-items: center;
  font-size: 14px;
  position: absolute;
  padding: 5px 0;
  margin-left: 70px;
}

.step-item {
  position: relative;
  width: 12px;
  height: 12px;
  color: #0081cc;
  flex-shrink: 0;
  flex-grow: 0;
  background: #fff;
  border-radius: 6px;
  text-align: center;
}

.step-desc {
  position: absolute;
  top: 23px;
  left: -50px;
  width: 100px;
  overflow: hidden;
  text-align: center;
  text-overflow: ellipsis;
  white-space: nowrap;
  font-size: 12px;
  color: #333;
}

.step-item.stepDone {
  background: #0081cc;
  color: #fff;
}

.step-item.stepDoing {
  background: #0081cc;
  box-shadow: 0 0 0 3px rgba(0, 129, 204, 0.3);
}

.step-item.stepUndo {
  background: #c1c0c1;
}

.step-line {
  height: 1px;
  width: 100px;
  background-color: #ccc;
  flex-shrink: 0;
  flex-grow: 0;
}

.step-line.stepDone {
  background-color: #0081cc;
}
.step-new .step-item.stepDone {
  background: #3fb992;
  color: #fff;
}
.step-new .step-line.stepDone{
  background-color: #3fb992;
}
.step-new .step-item.stepDoing {
  background: #2bb1ff;
  box-shadow: 0 0 0 3px rgba(43, 177, 255, 0.3);
}
.step-new .step-item.stepUndo {
  background: #e1e1e1;
}
.step-new .step-line{
  background-color: #e1e1e1;
}
