styles:
.tabs {
      padding: 15px 20px 0;
      border-bottom: solid 1px #d6d6d6
    }
    .tabs2 {
      padding: 15px 20px 0;
    }

    .tabs>div {
      margin-right: 70px
    }
    .tabs2>div {
      margin-right: 20px;
    }

    .title {
      cursor: pointer;
      display: table;
      position: relative;
      bottom: 0;
      font-size: 18px;
      height: 36px;
      color: #666;
    }

    .title2 {
      cursor: pointer;
      display: flex;
      position: relative;
      bottom: 0;
      justify-content: center;
      border: solid 1px #e2e2e2;
      font-size: 14px;
      width: 110px;
      border-radius: 2px;
      height: 25px;
      color: #0081cc;
    }
    .title2:hover {
      border: solid 1px #0081cc;
    }

    .title.active{
      font-weight: bold;
      color: #333;
      border-bottom: solid 2px #333;
      bottom: -1px;
    }

    .title2.active{
      color: #ffffff;
      background: #0081cc;
      bottom: -1px;
    }

    .title>div,.title2>div {
      display: table-cell;
      vertical-align: middle;
    }

    .title.active>div,.title2.active>div {
      padding-bottom: 3px
    }

    .title>div.icon,.title2>div.icon {
      padding: 0 10px 5px 0
    }

    .title>i,.title2>i {
      font-size: 10px;
      font-weight: 400;
      position: absolute;
      left: calc(100% + 7px);
      top: 10px;
      display: block;
      padding: 0 8px;
      line-height: 14px;
      height: 16px;
      color: #fff;
      border-radius: 10px;
      font-style: normal
    }

    .title.active>i,.title2.active>i {
        top: 9px
    }
