styles:
.canSort {
  cursor: pointer
}

.table-title {
  font-size: 14px;
}

table {
  border: none;
}

table thead, table tbody {
  display: table;
}

table > div {
  display: block;
  overflow-x: hidden;
  overflow-y: auto;
}

table th {
  color: #333;
  background-color: #fff;
  border-bottom: 1px solid #cccccc;
}

table td {
  border-bottom: 1px solid #e2e2e2;
  border-top: none;
}

.sort {
  background-image: url(/assets/img/sort.png);
  background-repeat: no-repeat;
  background-position: center;
  width: 20px;
  height: 13px;
  display: inline-block;
}

.sort-desc {
  background-image: url(/assets/img/sort.png);
  background-repeat: no-repeat;
  background-position: center;
  width: 20px;
  height: 13px;
  transform: rotate(180deg);
}

.table-title-th {
  display: flex;
}

.table-title-th > div {
  display: inline-block;
  margin-right: 5px
}

.table {
  font-size: 12px;
  color: #666 !important;
}

.table th {
  font-size: 14px;
  color: #333 !important;
  line-height: 13px;
}

