  font-size: 12px;
  color: #666;
  font-weight: normal
}

.hasFilter {
  background: url('data:image/png;base64,') no-repeat left bottom;
}

.noDatas {
  min-width: 100px;
}

.filterBtn {
  width: 100%;
  height: 100%;
  display: block;
  cursor: pointer;
}

.filter-box {
  position: absolute;
  left: 0;
  top: 20px;
  display: none;
  background: #fff;
  padding: 5px;
  box-shadow: 0px 0px 2px 1px #ccc;
  text-indent: 0;
  border-radius: 2px;
  white-space: nowrap;
  z-index: 10;
}

.filter-box.offset-left {
  right: 0;
  left: auto;
}

.filter-box > input[type=text] {
  width: 159px;
  height: 26px;
  border: solid 1px #ccc;
  padding: 0;
  margin: 0;
  background: #fff;
  transition: none;
  text-indent: 5px;
  font-size: 14px;
  font-weight: normal;
  color: #666;
  border-radius: 2px;
  font-size: 12px
}

.filter-box > input:focus {
  border-color: #0081cc
}

.filter-box > span {
  display: inline-block;
  font-size: 14px;
  line-height: 25px;
  text-indent: 7px
}

.filter-box > input[type=checkbox] {
  position: relative;
  display: none;
  bottom: -2px;
  cursor: pointer;
}

.filterSubmit {
  padding: 0 20px;
  text-align: center;
  margin: 5px auto;
  color: #fff;
  width: 94px;
  height: 26px;
  line-height: 23px;
  font-size: 14px;
  background: #0081cc;
  border-radius: 10px;
  box-shadow: 0px 0px 1px 1px #ccc;
  cursor: pointer;
}

.filterSubmit:hover {
  background: #0067a3
}

.filterSubmit:active {
  box-shadow: none
}

.hasSubmit {
  color: #000000;
  background: #ccc;
  box-shadow: 0px 0px 1px 1px #ccc;
}

th.check-box {
  width: 33px;
}

.table th,
.table td {
  text-align: left;
  border-top: none;
  padding: 18px 0 13px;
  text-indent: 20px;
  height: 14px;
  line-height: 14px
}

.check-box, td {
  position: relative;
}

input[type=checkbox] {
  visibility: hidden;
}

.check-box label,
td label {
  cursor: pointer;
  position: absolute;
  width: 18px;
  height: 18px;
  top: 18px;
  left: 15px;
  background: #fff;
  border: 1px solid #c8c8c8;
  -moz-border-radius: 3px; /* Gecko browsers */
  -webkit-border-radius: 3px; /* Webkit browsers */
  border-radius: 3px; /* W3C syntax */
}

.filter-box label {
  display: inline-block;
  position: relative;
  cursor: pointer;
  width: 16px;
  height: 16px;
  top: 10px;
  left: 5px;
  background: #fff;
  border: 1px solid #c8c8c8;
  -moz-border-radius: 3px; /* Gecko browsers */
  -webkit-border-radius: 3px; /* Webkit browsers */
  border-radius: 3px; /* W3C syntax */
}

.check-box label:hover,
.filter-box label:hover,
td label:hover {
  background: #0081CC;
}

.check-box label:after,
td label:after {
  opacity: 0;
  content: '';
  position: absolute;
  width: 9px;
  height: 5px;
  background: transparent;
  top: 4px;
  left: 4px;
  border: 2px solid #fff;
  border-top: none;
  border-right: none;
  -webkit-transform: rotate(-45deg);
  -moz-transform: rotate(-45deg);
  -o-transform: rotate(-45deg);
  -ms-transform: rotate(-45deg);
  transform: rotate(-45deg);
}

.filter-box label:after {
  opacity: 0;
  content: '';
  position: absolute;
  width: 9px;
  height: 5px;
  background: transparent;
  top: 3px;
  left: 3px;
  border: 2px solid #fff;
  border-top: none;
  border-right: none;
  -webkit-transform: rotate(-45deg);
  -moz-transform: rotate(-45deg);
  -o-transform: rotate(-45deg);
  -ms-transform: rotate(-45deg);
  transform: rotate(-45deg);
}

.check-box label:hover::after,
.filter-box label:hover::after,
td label:hover::after {
  opacity: 0.6;
}

input[type=checkbox]:disabled + label {
  cursor: not-allowed;
  background-color: #f5f5f5;
  opacity: 0.4;
}

input[type=checkbox]:disabled + label:hover {
  background: #ffffff;
}

input[type=checkbox]:checked + label {
  background: #0081CC;
}

input[type=checkbox]:checked + label:after {
  opacity: 1.0;
}

tbody tr.bgc:nth-child(odd) {
  background-color: #f8f8f8;
}

tbody tr.bgc:nth-child(even) {
  background-color: #ffffff;
}

tbody tr:hover {
  background-color: #edf0f5;
}

tbody tr.bgc:hover {
  background-color: #edf0f5;
}
