styles:
li {
  list-style: none;
  margin: 0;
  padding: 0;
}

.sub-node-container {
}

.before-node {
}

.left-line {
  position: absolute;
  width: 1px;
  height: 100000px;
  border-left: 1px solid red;
}

.node-container {
  /*position: relative;*/
}

.tree-expand {
  cursor: pointer;
  min-width: 20px;
  flex-grow: 0;
  background: url("data:image/png;base64,") no-repeat center transparent;
}

.tree-expand.tree-no-child {
  cursor: default;
  background: none;
}

.tree-expanded {
  background: url("data:image/png;base64,") no-repeat center transparent;
}

.noIcon {
  margin-left: 10px;
}

.tree-icon {
  display: inline-block;
  min-width: 20px;
  height: 20px;
  flex-grow: 0;
}

.tree-icon-config {
  min-width: 20px;
  margin: 0 0 0 10px;
  background-repeat: no-repeat;
  background-position: center;
  background-size: contain;
  flex-grow: 0;
}

.tree-checkbox-box {
  display: block;
  position: relative;
  min-width: 20px;
  margin: 0;
  text-align: center;
  flex-grow: 0;
}

.tree-checkbox-box input {
  margin-top: 10px;
}

.tree-option-box {
  position: absolute;
  right: 20px;
  height: 30px;
  display: none;
}

.tree-node-list {
  position: relative;
  height: 30px;
  line-height: 30px;
}

.tree-node-list:hover,
.tree-node-list.tree-node-selected {
  color: #0081cc;
  background-color: #edf0f5;
}

.tree-node-list:hover .tree-option-box {
  display: block;
}

.tree-node-list .tree-node-text.active{
  color: #0067A3;
}
input[type=checkbox] {
  visibility: hidden;
}

.clabel {
  cursor: pointer;
  position: absolute;
  width: 18px;
  height: 18px;
  top: 7px;
  left: 4px;
  background: #fff;
  border: 1px solid #c8c8c8;
  -moz-border-radius: 3px; /* Gecko browsers */
  -webkit-border-radius: 3px; /* Webkit browsers */
  border-radius: 3px; /* W3C syntax */
}

.clabel:hover {
  background: #0081CC;
}

.clabel:after {
  opacity: 0;
  content: '';
  position: absolute;
  width: 9px;
  height: 5px;
  background: transparent;
  top: 4px;
  left: 4px;
  border: 2px solid #fff;
  border-top: none;
  border-right: none;
  -webkit-transform: rotate(-45deg);
  -moz-transform: rotate(-45deg);
  -o-transform: rotate(-45deg);
  -ms-transform: rotate(-45deg);
  transform: rotate(-45deg);
}

.clabel:hover:after {
  opacity: 0.6;
}

input.half-checked[type=checkbox] + .clabel {
  background: #fff;
}

input.half-checked[type=checkbox] + .clabel:after {
  opacity: 1;
  content: '';
  position: absolute;
  width: 11px;
  height: 7px;
  background: #0081CC;
  top: 3px;
  left: 3px;
  border: 5px solid #0081CC;
  -webkit-transform: none;
  -moz-transform: none;
  -o-transform: none;
  -ms-transform: none;
  transform: none;
}

input[type=checkbox]:checked + label {
  background: #0081CC;
}

input[type=checkbox]:checked + label:after {
  opacity: 1.0;
}

.tree-options-btn {
  overflow: hidden;
  cursor: pointer;
  margin-left: 5px;
  text-align: center;
  min-width: 20px;
  white-space: nowrap;
  background-repeat: no-repeat;
  background-position: center;
}

.tree-more-btn-box {
  display: none;
  position: absolute;
  right: 1px;
  top: 30px;
  background-color: #fff;
  z-index: 20;
  box-shadow: 3px 3px 10px 0 rgba(0, 0, 0, .2);
}

.tree-more-btn {
  text-indent: -999px;
}

.tree-more-btn-box li {
  padding: 0 10px;
  white-space: nowrap;
  text-align: center;
  color: #565656;
  cursor: pointer;
}

.cursor-pointer {
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
}

.tree-more-btn-box li:first-child {
  border-radius: 3px 3px 0 0;
}

.tree-more-btn-box li:last-child {
  border-radius: 0 0 3px 3px;
}

.tree-more-btn-box li:hover {
  background-color: #0081cc;
  color: #fff;
}
